"use client"

import { FieldValues, SubmitHandler, useForm } from "react-hook-form";

import { BaseButton } from "../../atoms";
import { FormField } from "../../molecules";
import { handleRegister } from "../../../services/auth";
import { LoginCredentials } from "../../../models/auth";
import { useNavigate } from "react-router-dom";

const SignUp = () => {
    const navigate = useNavigate();
    const { register, handleSubmit } = useForm<FieldValues>();

    const onSubmit: SubmitHandler<FieldValues> = async (data) => {
        await handleRegister(data as LoginCredentials)
            .then(res => {
                console.log('onSubmit then', res);
                navigate("/signin");
            })
            .catch(error => {
                console.error('onSubmit catch', error);
            })
            .finally(() => {

            });
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)} className="flex flex-col gap-4">
            <FormField
                name="fullname"
                label="Full name"
                placeholder="Enter your name"
                register={register} />
            <FormField
                name="username"
                label="Username"
                placeholder="@username"
                register={register} />
            <FormField
                type="email"
                name="email"
                label="Email"
                placeholder="Enter your email address"
                register={register} />
            <FormField
                type="password"
                name="password"
                label="Password"
                placeholder="Enter your password"
                register={register} />
            <BaseButton type="submit" label="Sign in" />
            <div className="flex items-center mx-auto">
                <input id="remember_me" type="checkbox" className="border border-gray-300 text-red-600 shadow-sm focus:border-red-300 focus:ring focus:ring-red-200 focus:ring-opacity-50" />
                <label htmlFor="remember_me" className="ml-2 block text-sm leading-5 text-gray-900"> I agree to the terms and conditions</label>
            </div>
        </form>
    );
}

export default SignUp;
